def int_to_binary_matrix(val):
    # Convert an integer to its 8-bit binary representation, then to a matrix
    bin_repr = format(val, '08b')
    return [[int(bit) for bit in bin_repr]]

def draw_sprite(screen, x, y, sprite_data, sprite_color, transparent: bool = False):
    for quadrant, (qx, qy) in zip(sprite_data, [(0, 0), (8, 0), (0, 8), (8, 8)]):
        for row, vals in enumerate(quadrant):
            matrix = int_to_binary_matrix(vals)
            for col, bit in enumerate(matrix[0]):
                if transparent:
                    if bit: screen.fill(sprite_color, (x + qx + col, y + qy + row, 1, 1))
                else:
                    color = sprite_color if bit else BLACK
                    screen.fill(color, (x + qx + col, y + qy + row, 1, 1))

def draw_font_sprite(screen, x, y, sprite_data, sprite_color):
    for row, vals in enumerate(sprite_data):
        matrix = int_to_binary_matrix(vals)
        for col, bit in enumerate(matrix[0][:8]):  # Only taking the first 8 bits
            color = sprite_color if bit else BLACK
            screen.fill(color, (x + col, y + row, 1, 1))        

